sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	var emailExists = ("u_requested_for_email" in source);
	if(!emailExists) {
		var name = source.u_requested_for;
		var lcName = name.toLowerCase();
		var nameWOSpace = lcName.replace(' ','.');
		var exampleEmail = nameWOSpace + '@example.com';
		target.u_requested_for_email = exampleEmail;

		//target.u_requested_for_email = source.u_requested_for.toLowerCase().replace(' ','.') + '@example.com';

	}


})(source, map, log, target);
